import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SCALE = 0.01; // 1 cm = 0.01 px => 1 m = 1 px aprox

function App() {
  const [circuit, setCircuit] = useState(null);
  const [pathData, setPathData] = useState('');
  const [startMarker, setStartMarker] = useState(null);
  const [selectedCircuitId, setSelectedCircuitId] = useState(1);

  useEffect(() => {
    axios.get(`http://localhost:3001/circuit/${selectedCircuitId}`)
      .then(res => {
        setCircuit(res.data);
        const { path, startLine } = generateCircuitSVG(res.data.sectors, res.data.mini_sectors);
        setPathData(path);
        setStartMarker(startLine);
      })
      .catch(console.error);
  }, [selectedCircuitId]);

  const generateCircuitSVG = (sectors, allMiniSectors) => {
    if (!sectors || sectors.length === 0) return { path: '', startLine: null };

    const firstSector = sectors[0];
    const firstMiniSectorId = firstSector.mini_sector_ids[0];
    const firstMini = allMiniSectors.find(ms => ms.id === firstMiniSectorId);

    let x = 300;
    let y = 200;
    let angle = 0;
    let d = `M ${x} ${y}`;

    let startLine = null;

    for (let sector of allMiniSectors) {
      const isCurve = sector.curvature_radius_cm > 0;

      if (sector.id === firstMini.id) {
        startLine = { x, y, angle };
      }

      if (!isCurve) {
        const length = sector.length_cm * SCALE;
        const dx = Math.cos(angle * Math.PI / 180) * length;
        const dy = Math.sin(angle * Math.PI / 180) * length;

        x += dx;
        y += dy;
        d += ` L ${x} ${y}`;
        angle = sector.direction ?? angle;
      } else {
        const radius = sector.curvature_radius_cm * SCALE;
        const sweepFlag = sector.curve_direction === 'clockwise' ? 1 : 0;

        const arcAngleDeg = sector.arc_angle_deg ?? 180;
        const arcAngleRad = arcAngleDeg * Math.PI / 180;

        // Calcular centro del arco
        const normalAngleRad = (angle + (sweepFlag === 1 ? 90 : -90)) * Math.PI / 180;
        const cx = x + radius * Math.cos(normalAngleRad);
        const cy = y + radius * Math.sin(normalAngleRad);

        // Ángulo relativo desde el centro hasta el punto final
        const startAngle = (angle - 90) * Math.PI / 180;
        const endAngle = startAngle + (sweepFlag === 1 ? arcAngleRad : -arcAngleRad);

        const endX = cx + radius * Math.cos(endAngle + Math.PI / 2);
        const endY = cy + radius * Math.sin(endAngle + Math.PI / 2);

        const largeArcFlag = arcAngleDeg > 180 ? 1 : 0;

        d += ` A ${radius} ${radius} 0 ${largeArcFlag} ${sweepFlag} ${endX} ${endY}`;

        x = endX;
        y = endY;
        angle = (angle + (sweepFlag === 1 ? arcAngleDeg : -arcAngleDeg) + 360) % 360;
      }
    }

    return { path: d, startLine };
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Circuit Viewer</h1>

      {circuit ? (
        <>
        <div style={{ marginBottom: '1rem' }}>
  <button onClick={() => setSelectedCircuitId(1)} disabled={selectedCircuitId === 1}>
    Circuito 1
  </button>
  <button onClick={() => setSelectedCircuitId(2)} disabled={selectedCircuitId === 2} style={{ marginLeft: '1rem' }}>
    Circuito 2
  </button>
</div>
          <h2>{circuit.circuit.name}</h2>
          <svg
  viewBox="0 0 1600 800"
  width="100%"
  height="600"
  style={{ border: '1px solid black', marginBottom: '1rem' }}
>
  {/* Circuito gris más grueso */}
  <path d={pathData} stroke="#555" strokeWidth="5" fill="none" />

  {/* Línea de meta más larga y gruesa */}
  {startMarker && (
    <line
      x1={startMarker.x + 10 * Math.cos((startMarker.angle + 90) * Math.PI / 180)}
      y1={startMarker.y + 10 * Math.sin((startMarker.angle + 90) * Math.PI / 180)}
      x2={startMarker.x + 10 * Math.cos((startMarker.angle - 90) * Math.PI / 180)}
      y2={startMarker.y + 10 * Math.sin((startMarker.angle - 90) * Math.PI / 180)}
      stroke="black"
      strokeWidth="4"
    />
  )}
</svg>

          <h3>Mini Sectores</h3>
          <ul>
            {circuit.mini_sectors.map(ms => (
              <li key={ms.id}>
                <strong>{ms.name}</strong>: {ms.length_cm / 100} m, 
                {ms.curvature_radius_cm > 0
                  ? ` curva (${ms.curve_direction}, radio ${ms.curvature_radius_cm / 100} m)`
                  : ` recta (dirección ${ms.direction}°)`
                }
              </li>
            ))}
          </ul>

          <h3>Sectores</h3>
          <ul>
            {circuit.sectors.map(s => (
              <li key={s.id}>
                <strong>{s.name}</strong>: Mini sectores {s.mini_sector_ids.join(', ')}
              </li>
            ))}
          </ul>
        </>
      ) : (
        <p>Loading circuit...</p>
      )}
    </div>
  );
}

export default App;
