import React from 'react';

export default function CircuitSVG({ pathData, bounds, startLine, debugPoints, carPosition }) {
  const viewWidth = 1000;
  const viewHeight = 600;
  const padding = 150;

  if (!pathData || !bounds) return null;

  const contentWidth = bounds.maxX - bounds.minX;
  const contentHeight = bounds.maxY - bounds.minY;

  const scale = Math.min(
    (viewWidth - 2 * padding) / contentWidth,
    (viewHeight - 2 * padding) / contentHeight
  );

  const offsetX = -bounds.minX;
  const offsetY = -bounds.minY;

  return (
    <svg
      width={viewWidth}
      height={viewHeight}
      viewBox={`0 0 ${viewWidth} ${viewHeight}`}
      style={{ border: '1px solid #ccc' }}
    >
      <g transform={`translate(${padding}, ${padding}) scale(${scale}) translate(${offsetX}, ${offsetY})`}>
        <path d={pathData} stroke="gray" strokeWidth={5} fill="none" />

        {startLine && (
          <line
            x1={startLine.x + 10 * Math.cos((startLine.angle + 90) * Math.PI / 180)}
            y1={startLine.y + 10 * Math.sin((startLine.angle + 90) * Math.PI / 180)}
            x2={startLine.x + 10 * Math.cos((startLine.angle - 90) * Math.PI / 180)}
            y2={startLine.y + 10 * Math.sin((startLine.angle - 90) * Math.PI / 180)}
            stroke="black"
            strokeWidth={4}
          />
        )}

        {debugPoints.map((pt, i) => (
          <g key={i}>
            <circle cx={pt.x} cy={pt.y} r="4" fill="red" />
            <text x={pt.x + 6} y={pt.y - 6} fontSize="12" fill="black">{i}</text>
          </g>
        ))}

        {carPosition && (
          <circle cx={carPosition.x} cy={carPosition.y} r="6" fill="blue" />
        )}
      </g>
    </svg>
  );
}
