// src/App.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { generateCircuitSVG } from './utils/generateCircuitSVG';
import CircuitSVG from './components/CircuitSVG';

function App() {
  const [circuit, setCircuit] = useState(null);
  const [pathData, setPathData] = useState('');
  const [startMarker, setStartMarker] = useState(null);
  const [debugPoints, setDebugPoints] = useState([]);
  const [bounds, setBounds] = useState(null);
  const [selectedCircuitId, setSelectedCircuitId] = useState(1);
  const [availableCircuits, setAvailableCircuits] = useState([]);

  const [isRunning, setIsRunning] = useState(false);
  const [carPosition, setCarPosition] = useState(null);
  const [elapsedTime, setElapsedTime] = useState(0);
  const [customSpeed, setCustomSpeed] = useState(100);
  const [completed, setCompleted] = useState(false);
  const intervalRef = useRef(null);
  const carPath = useRef([]);
  const indexRef = useRef(0);
  const totalLengthRef = useRef(0);
  const [lengthReady, setLengthReady] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:3001/circuits/${selectedCircuitId}`)
      .then(res => {
        setCircuit(res.data);
        const { path, startLine, debugPoints, bounds } = generateCircuitSVG(
          res.data.sectors,
          res.data.mini_sectors
        );
        setPathData(path);
        setStartMarker(startLine);
        setDebugPoints(debugPoints);
        setBounds(bounds);
        setCarPosition(null);
        setElapsedTime(0);
        setIsRunning(false);
        setCompleted(false);
        setLengthReady(false);
        indexRef.current = 0;
        if (intervalRef.current) clearInterval(intervalRef.current);

        setTimeout(() => {
          const svgPath = document.querySelector('path');
          if (!svgPath) return;
          const length = svgPath.getTotalLength();
          totalLengthRef.current = length;
          setLengthReady(true);

          const points = [];
          let currentLength = 0;

          for (let sector of res.data.mini_sectors) {
            const lengthMeters = sector.length_cm / 100;
            const isCurve = sector.curvature_radius_cm > 0;

            let speedFactor = 1;
            if (isCurve) {
              const radius = sector.curvature_radius_cm / 100;
              speedFactor = (radius - 100) / (300 - 100) * (0.99 - 0.5) + 0.5;
              speedFactor = Math.min(0.99, Math.max(0.5, speedFactor));
            }

            const velocityMps = (customSpeed * speedFactor) * 1000 / 3600;
            const intervalMs = 30;
            const distancePerInterval = velocityMps * (intervalMs / 1000);
            const steps = Math.max(1, Math.min(300, Math.floor(lengthMeters / distancePerInterval)));

            for (let i = 0; i < steps; i++) {
              const pos = svgPath.getPointAtLength(currentLength + i * (lengthMeters / steps));
              points.push({ x: pos.x, y: pos.y });
            }

            currentLength += lengthMeters;
          }

          carPath.current = points;
        }, 100);
      })
      .catch(console.error);
  }, [selectedCircuitId, customSpeed]);

  useEffect(() => {
    axios.get('http://localhost:3001/circuits')
      .then(res => setAvailableCircuits(res.data))
      .catch(console.error);
  }, []);

  const startSimulation = () => {
    if (!carPath.current.length) return;

    setIsRunning(true);
    setCompleted(false);
    const intervalMs = 30;
    intervalRef.current = setInterval(() => {
      const i = indexRef.current;
      if (i >= carPath.current.length) {
        clearInterval(intervalRef.current);
        setIsRunning(false);
        setCompleted(true);
        return;
      }
      setCarPosition(carPath.current[i]);
      setElapsedTime(prev => prev + intervalMs);
      indexRef.current += 1;
    }, intervalMs);
  };

  const stopSimulation = () => {
    clearInterval(intervalRef.current);
    setIsRunning(false);
  };

  const resetSimulation = () => {
    clearInterval(intervalRef.current);
    setIsRunning(false);
    setElapsedTime(0);
    setCarPosition(null);
    setCompleted(false);
    indexRef.current = 0;
  };

  const formatTime = (ms) => {
    const totalSeconds = Math.floor(ms / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;
    return hours > 0
      ? `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`
      : `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  };

  const formatDistance = (meters) => `${meters.toFixed(0)} m`;

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Circuit Viewer</h1>

      <div style={{ marginBottom: '1rem' }}>
        {availableCircuits.map(c => (
          <button
            key={c.id}
            onClick={() => setSelectedCircuitId(c.id)}
            disabled={selectedCircuitId === c.id}
            style={{ marginRight: '0.5rem' }}
          >
            {c.name}
          </button>
        ))}
        <input
          type="number"
          value={customSpeed}
          onChange={(e) => setCustomSpeed(parseInt(e.target.value) || 0)}
          style={{ width: '80px', marginLeft: '1rem' }}
        /> km/h
        <button onClick={startSimulation} style={{ marginLeft: '1rem' }}>
          {isRunning ? 'Resume' : 'Start'}
        </button>
        <button onClick={stopSimulation} disabled={!isRunning} style={{ marginLeft: '0.5rem' }}>
          Stop
        </button>
        <button onClick={resetSimulation} style={{ marginLeft: '0.5rem' }}>
          Reset
        </button>
      </div>

      {circuit && (
        <div style={{ display: 'flex' }}>
          <div style={{ flex: 1 }}>
            <h2>
              {circuit.circuit.name}{' '}
              {lengthReady && (
                <span>— Longitud total: {formatDistance(totalLengthRef.current)}</span>
              )}
            </h2>
            <CircuitSVG
              pathData={pathData}
              bounds={bounds}
              startLine={startMarker}
              debugPoints={debugPoints}
              carPosition={carPosition}
            />
          </div>
          <div style={{ width: '300px', paddingLeft: '2rem' }}>
            <h3>Datos en tiempo real</h3>
            {isRunning && (
              <>
                <p><strong>Tiempo:</strong> {formatTime(elapsedTime)}</p>
                <p><strong>Velocidad:</strong> {customSpeed} km/h</p>
                <p><strong>Distancia:</strong> {formatDistance(
                  (indexRef.current / carPath.current.length) * totalLengthRef.current || 0
                )}</p>
              </>
            )}
            {completed && (
              <p style={{ color: 'green' }}><strong>¡Vuelta completada en {formatTime(elapsedTime)}!</strong></p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
