// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { generateCircuitSVG } from './utils/generateCircuitSVG';
import CircuitSVG from './components/CircuitSVG';

function App() {
  const [circuit, setCircuit] = useState(null);
  const [pathData, setPathData] = useState('');
  const [startMarker, setStartMarker] = useState(null);
  const [debugPoints, setDebugPoints] = useState([]);
  const [bounds, setBounds] = useState(null);
  const [selectedCircuitId, setSelectedCircuitId] = useState(1);
  const [availableCircuits, setAvailableCircuits] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:3001/circuits/${selectedCircuitId}`)
      .then(res => {
        console.log('✅ Datos del circuito:', res.data);
        setCircuit(res.data);
        const { path, startLine, debugPoints, bounds } = generateCircuitSVG(
          res.data.sectors,
          res.data.mini_sectors
        );
        setPathData(path);
        setStartMarker(startLine);
        setDebugPoints(debugPoints);
        setBounds(bounds);
      })
      .catch(err => {
        console.error('❌ Error cargando circuito:', err);
      });
  }, [selectedCircuitId]);

  useEffect(() => {
    axios.get('http://localhost:3001/circuits')
      .then(res => {
        console.log('✅ Lista de circuitos:', res.data);
        setAvailableCircuits(res.data);
      })
      .catch(err => {
        console.error('❌ Error cargando circuitos:', err);
      });
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Circuit Viewer</h1>

      {availableCircuits.length === 0 && <p>Cargando circuitos...</p>}

      <div style={{ marginBottom: '1rem' }}>
        {availableCircuits.map(c => (
          <button
            key={c.id}
            onClick={() => setSelectedCircuitId(c.id)}
            disabled={selectedCircuitId === c.id}
            style={{ marginRight: '0.5rem' }}
          >
            {c.name}
          </button>
        ))}
      </div>

      {circuit?.circuit && (
        <>
          <h2>{circuit.circuit.name}</h2>
          <CircuitSVG
            pathData={pathData}
            bounds={bounds}
            startLine={startMarker}
            debugPoints={debugPoints}
          />

          <h3>Mini Sectores</h3>
          <ul>
            {circuit.mini_sectors.map(ms => (
              <li key={ms.id}>
                <strong>{ms.name}</strong>: {ms.length_cm / 100} m,{' '}
                {ms.curvature_radius_cm > 0
                  ? `curva (${ms.curve_direction}, radio ${ms.curvature_radius_cm / 100} m, ángulo ${ms.arc_angle_deg ?? 180}°)`
                  : `recta (dirección ${ms.direction}°)`}
              </li>
            ))}
          </ul>

          <h3>Sectores</h3>
          <ul>
            {circuit.sectors.map(s => (
              <li key={s.id}>
                <strong>{s.name}</strong>: Mini sectores {s.mini_sector_ids.join(', ')}
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default App;
