import React from 'react';

export default function CircuitSVG({ pathData, bounds, startLine, carPosition, sectorEndPoints }) {
  const viewWidth = 1000;
  const viewHeight = 600;
  const padding = 150;

  if (!pathData || !bounds) return null;

  const contentWidth = bounds.width;
  const contentHeight = bounds.height;

  const scale = Math.min(
    (viewWidth - 2 * padding) / contentWidth,
    (viewHeight - 2 * padding) / contentHeight
  );

  // Función para dibujar línea perpendicular centrada en (x, y)
  const drawMarkerLine = (x, y, angleDeg, length = 20, stroke = "black", width = 4) => {
    const rad = (angleDeg * Math.PI) / 180;
    const dx = (length / 2) * Math.cos(rad + Math.PI / 2);
    const dy = (length / 2) * Math.sin(rad + Math.PI / 2);
    return (
      <line
        key={`${x}-${y}-${stroke}`}
        x1={x - dx}
        y1={y - dy}
        x2={x + dx}
        y2={y + dy}
        stroke={stroke}
        strokeWidth={width}
      />
    );
  };

  // Calcula el ángulo entre dos puntos
  const angleBetween = (p1, p2) => {
    const dx = p2.x - p1.x;
    const dy = p2.y - p1.y;
    return (Math.atan2(dy, dx) * 180) / Math.PI;
  };

  return (
    <svg
      width={viewWidth}
      height={viewHeight}
      viewBox={`0 0 ${viewWidth} ${viewHeight}`}
      style={{ border: '1px solid #ccc' }}
    >
      <g transform={`translate(${padding}, ${viewHeight - padding}) scale(${scale}, -${scale})`}>
  {/* Circuito */}
  <path d={pathData} stroke="gray" strokeWidth={15} fill="none" />

  {/* Línea roja de meta al inicio del circuito */}
  {startLine && carPosition && (
    drawMarkerLine(
      carPosition.x,
      carPosition.y,
      angleBetween(carPosition, sectorEndPoints[0] || carPosition),
      40,
      'red',
      8
    )
  )}

  {/* Líneas negras al final de cada sector */}
  {sectorEndPoints?.map((p, i, arr) => {
    const prev = arr[i - 1] ?? carPosition; // usamos carPosition como referencia inicial
    const angle = angleBetween(prev, p);
    return drawMarkerLine(p.x, p.y, angle, 20, 'black', 4);
  })}

  {/* Auto */}
  {carPosition && (
    <circle cx={carPosition.x} cy={carPosition.y} r="15" fill="blue" />
  )}
</g>
    </svg>
  );
}
