import React, { useEffect, useState } from 'react';
import axios from 'axios';

const SCALE = 0.01; // 1 cm = 0.01 px => 1 m = 1 px aprox

function App() {
  const [circuit, setCircuit] = useState(null);
  const [pathData, setPathData] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3001/circuit/1')
      .then(res => {
        setCircuit(res.data);
        const path = generateSVGPath(res.data.mini_sectors);
        setPathData(path);
      })
      .catch(console.error);
  }, []);

  const generateSVGPath = (sectors) => {
    if (!sectors || sectors.length === 0) return '';

    let x = 300;
    let y = 200;
    let angle = 0; // dirección inicial
    let d = `M ${x} ${y}`;

    for (let sector of sectors) {
      const isCurve = sector.curvature_radius_cm > 0;

      if (!isCurve) {
        const length = sector.length_cm * SCALE;
        const dx = Math.cos(angle * Math.PI / 180) * length;
        const dy = Math.sin(angle * Math.PI / 180) * length;

        x += dx;
        y += dy;
        d += ` L ${x} ${y}`;
      } else {
        const radius = sector.curvature_radius_cm * SCALE;
        const sweepFlag = sector.curve_direction === 'clockwise' ? 1 : 0;

        // Centro del arco relativo al ángulo actual
        const normalAngle = angle + (sweepFlag === 1 ? 90 : -90);
        const cx = x + radius * Math.cos(normalAngle * Math.PI / 180);
        const cy = y + radius * Math.sin(normalAngle * Math.PI / 180);

        // Punto final del arco
        const endAngle = angle + (sweepFlag === 1 ? 180 : -180);
        const x1 = cx + radius * Math.cos((endAngle + (sweepFlag === 1 ? -90 : 90)) * Math.PI / 180);
        const y1 = cy + radius * Math.sin((endAngle + (sweepFlag === 1 ? -90 : 90)) * Math.PI / 180);

        d += ` A ${radius} ${radius} 0 0 ${sweepFlag} ${x1} ${y1}`;

        x = x1;
        y = y1;
        angle = endAngle % 360;
      }
    }

    return d;
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Circuit Viewer</h1>

      {circuit ? (
        <>
          <h2>{circuit.circuit.name}</h2>
          <svg width="800" height="600" style={{ border: '1px solid black', marginBottom: '1rem' }}>
            <path d={pathData} stroke="black" strokeWidth="2" fill="none" />
          </svg>

          <h3>Mini Sectores</h3>
          <ul>
            {circuit.mini_sectors.map(ms => (
              <li key={ms.id}>
                <strong>{ms.name}</strong>: {ms.length_cm / 100} m, 
                {ms.curvature_radius_cm > 0
                  ? ` curva (${ms.curve_direction}, radio ${ms.curvature_radius_cm / 100} m)`
                  : ` recta (dirección ${ms.direction}°)`
                }
              </li>
            ))}
          </ul>

          <h3>Sectores</h3>
          <ul>
            {circuit.sectors.map(s => (
              <li key={s.id}>
                <strong>{s.name}</strong>: Mini sectores {s.mini_sector_ids.join(', ')}
              </li>
            ))}
          </ul>
        </>
      ) : (
        <p>Loading circuit...</p>
      )}
    </div>
  );
}

export default App;
