import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [circuit, setCircuit] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:3001/circuit/1')
      .then(res => setCircuit(res.data))
      .catch(console.error);
  }, []);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Circuit Viewer</h1>
      {circuit ? (
        <>
          <h2>{circuit.circuit.name}</h2>
          <svg width="600" height="400" style={{ border: '1px solid black', marginBottom: '1rem' }}>
            <line x1="100" y1="150" x2="500" y2="150" stroke="black" strokeWidth="2" />
            <path d="M 500 150 A 50 50 0 0 1 500 250" fill="none" stroke="black" strokeWidth="2" />
            <line x1="500" y1="250" x2="100" y2="250" stroke="black" strokeWidth="2" />
            <path d="M 100 250 A 50 50 0 0 1 100 150" fill="none" stroke="black" strokeWidth="2" />
          </svg>

          <h3>Mini Sectores</h3>
          <ul>
            {circuit.mini_sectors.map(ms => (
              <li key={ms.id}>
                <strong>{ms.name}</strong>: {ms.length_cm / 100} m, 
                {ms.curvature_radius_cm > 0 ? ` curva (${ms.curve_direction}, radio ${ms.curvature_radius_cm / 100} m)` : ` recta (direcci처n ${ms.direction}째)`}
              </li>
            ))}
          </ul>

          <h3>Sectores</h3>
          <ul>
            {circuit.sectors.map(s => (
              <li key={s.id}>
                <strong>{s.name}</strong>: Mini sectores {s.mini_sector_ids.join(', ')}
              </li>
            ))}
          </ul>
        </>
      ) : (
        <p>Loading circuit...</p>
      )}
    </div>
  );
}

export default App;
